// Service to fetch satellite data from the backend
const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

export async function fetchMODISData() {
  const response = await fetch(`${API_BASE_URL}/api/modis`);
  if (!response.ok) {
    throw new Error('Failed to fetch MODIS data');
  }
  return response.json();
}

export async function fetchVIIRSData() {
  const response = await fetch(`${API_BASE_URL}/api/viirs`);
  if (!response.ok) {
    throw new Error('Failed to fetch VIIRS data');
  }
  return response.json();
}

// Legacy function for backward compatibility
export const fetchSatelliteData = async (satellite) => {
  try {
    console.log(`Fetching ${satellite} satellite data from Flask backend...`);

    const response = await fetch(`${API_BASE_URL}/api/${satellite.toLowerCase()}`, {
      method: 'GET',
      headers: {
        'Accept': 'application/json',
        'Content-Type': 'application/json'
      }
    });

    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }

    const data = await response.json();

    console.log(`Received ${data.total || 0} ${satellite} hotspots from backend`);

    return data.hotspots || [];
  } catch (error) {
    console.error(`Failed to fetch ${satellite} satellite data:`, error);
    throw error;
  }
};
