// Service to fetch satellite data from the backend
const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000';

export async function fetchMODISData() {
  const response = await fetch(`${API_BASE_URL}/api/modis`);
  if (!response.ok) {
    throw new Error('Failed to fetch MODIS data');
  }
  return response.json();
}

export async function fetchVIIRSData() {
  const response = await fetch(`${API_BASE_URL}/api/viirs`);
  if (!response.ok) {
    throw new Error('Failed to fetch VIIRS data');
  }
  return response.json();
}
