const fireIcon = new Icon({
  iconUrl: 'data:image/svg+xml;base64,',
  iconSize: [32, 32],
  iconAnchor: [16, 32],
  popupAnchor: [0, -32],
});

// Sample fire data - replace with real data from your API
const sampleFires = [
  {
    id: 1,
    name: "Riverside Fire",
    lat: 37.7749,
    lng: -122.4194,
    size: 2500,
    containment: 25,
    severity: "High",
    lastUpdate: "2024-07-07T14:30:00Z",
    weather: {
